    def inner(function):
        _docs = docs
        if _docs != None and type(_docs) is not str: _docs = _docs.__doc__
        function.__doc__ = _docs or function.__doc__ or _class.__doc__
        setattr(_class, name or function.__qualname__.split(".")[-1], function)
        return function
